Value.value:
function include_merge_files_by_lines { : }
# 1: Filename of the "master" file # 2: Filename of the newly created file function create_empty_file_like { local lines_count lines_count=$(cat "$1" | wc -l) for _ in $(seq 1 "$lines_count"); do printf '\n' >> "$2" done }
# 1: Filename of the "master" file # 2: Filename of sample flie function second_file_is_same_except_newlines { local lines_of_master lines_of_sample len_of_master line_number i readarray -t lines_of_master < "$1" readarray -t lines_of_sample < "$2"
len_of_master="${#lines_of_master[@]}" if test "$len_of_master" != "${#lines_of_sample[@]}"; then echo "Files '$1' and '$2' have different number of lines, $len_of_master and ${#lines_of_sample[@]} respectively." return 1 fi
for line_number in $(seq 1 "$len_of_master"); do i=$((line_number - 1)) test -n "${lines_of_sample[$i]}" || continue if test "${lines_of_master[$i]}" != "${lines_of_sample[$i]}"; then echo "Line $line_number is different in files '$1' and '$2'." return 1 fi done }
# 1: Filename of the "master" file # 2: Filename of sample flie # 3: List of indices (1-based, space-separated string) function merge_first_lines_to_second_on_indices { local lines_of_master lines_of_sample line_number i test -f "$2" || create_empty_file_like "$1" "$2"
readarray -t lines_of_master < "$1" readarray -t lines_of_sample < "$2"
error_msg="$(second_file_is_same_except_newlines "$1" "$2")" if test $? != 0; then echo "Error merging lines into '$2': $error_msg" >&2 return 1 fi
for line_number in $3; do i=$((line_number - 1)) lines_of_sample[$i]="${lines_of_master[$i]}" done
printf "%s\n" "${lines_of_sample[@]}" > "$2" }

function include_mount_options_functions { : }
# $1: type of filesystem # $2: new mount point option # $3: filesystem of new mount point (used when adding new entry in fstab) # $4: mount type of new mount point (used when adding new entry in fstab) function ensure_mount_option_for_vfstype { local _vfstype="$1" _new_opt="$2" _filesystem=$3 _type=$4 _vfstype_points=() readarray -t _vfstype_points < <(grep -E "[[:space:]]${_vfstype}[[:space:]]" /etc/fstab | awk '{print $2}')
for _vfstype_point in "${_vfstype_points[@]}" do ensure_mount_option_in_fstab "$_vfstype_point" "$_new_opt" "$_filesystem" "$_type" done }
# $1: mount point # $2: new mount point option # $3: device or virtual string (used when adding new entry in fstab) # $4: mount type of mount point (used when adding new entry in fstab) function ensure_mount_option_in_fstab { local _mount_point="$1" _new_opt="$2" _device=$3 _type=$4 local _mount_point_match_regexp="" _previous_mount_opts="" _mount_point_match_regexp="$(get_mount_point_regexp "$_mount_point")"
if [ "$(grep -c "$_mount_point_match_regexp" /etc/fstab)" -eq 0 ]; then # runtime opts without some automatic kernel/userspace-added defaults _previous_mount_opts=$(grep "$_mount_point_match_regexp" /etc/mtab | head -1 | awk '{print $4}' \ | sed -E "s/(rw|defaults|seclabel|${_new_opt})(,|$)//g;s/,$//") [ "$_previous_mount_opts" ] && _previous_mount_opts+="," echo "${_device} ${_mount_point} ${_type} defaults,${_previous_mount_opts}${_new_opt} 0 0" >> /etc/fstab elif [ "$(grep "$_mount_point_match_regexp" /etc/fstab | grep -c "$_new_opt")" -eq 0 ]; then _previous_mount_opts=$(grep "$_mount_point_match_regexp" /etc/fstab | awk '{print $4}') sed -i "s|\(${_mount_point_match_regexp}.*${_previous_mount_opts}\)|\1,${_new_opt}|" /etc/fstab fi }
# $1: mount point function get_mount_point_regexp { printf "[[:space:]]%s[[:space:]]" "$1" }
# $1: mount point function assert_mount_point_in_fstab { local _mount_point_match_regexp _mount_point_match_regexp="$(get_mount_point_regexp "$1")" grep "$_mount_point_match_regexp" -q /etc/fstab \ || { echo "The mount point '$1' is not even in /etc/fstab, so we can't set up mount options" >&2; return 1; } }
# $1: mount point function remove_defaults_from_fstab_if_overriden { local _mount_point_match_regexp _mount_point_match_regexp="$(get_mount_point_regexp "$1")" if grep "$_mount_point_match_regexp" /etc/fstab | grep -q "defaults," then sed -i "s|\(${_mount_point_match_regexp}.*\)defaults,|\1|" /etc/fstab fi }
# $1: mount point function ensure_partition_is_mounted { local _mount_point="$1" mkdir -p "$_mount_point" || return 1 if mountpoint -q "$_mount_point"; then mount -o remount --target "$_mount_point" else mount --target "$_mount_point" fi }

function include_set_faillock_option { : }
function insert_preauth { local pam_file="$1" local option="$2" local value="$3" # is auth required pam_faillock.so preauth present? if grep -qE "^\s*auth\s+required\s+pam_faillock\.so\s+preauth.*$" "$pam_file" ; then # is the option set? if grep -qE "^\s*auth\s+required\s+pam_faillock\.so\s+preauth.*$option=([0-9]*).*$" "$pam_file" ; then # just change the value of option to a correct value sed -i --follow-symlinks "s/\(^auth.*required.*pam_faillock.so.*preauth.*silent.*\)\($option *= *\).*/\1\2$value/" "$pam_file" # the option is not set. else # append the option sed -i --follow-symlinks "/^auth.*required.*pam_faillock.so.*preauth.*silent.*/ s/$/ $option=$value/" "$pam_file" fi # auth required pam_faillock.so preauth is not present, insert the whole line else sed -i --follow-symlinks "/^auth.*sufficient.*pam_unix.so.*/i auth required pam_faillock.so preauth silent $option=$value" "$pam_file" fi }
function insert_authfail { local pam_file="$1" local option="$2" local value="$3" # is auth default pam_faillock.so authfail present? if grep -qE "^\s*auth\s+(\[default=die\])\s+pam_faillock\.so\s+authfail.*$" "$pam_file" ; then # is the option set? if grep -qE "^\s*auth\s+(\[default=die\])\s+pam_faillock\.so\s+authfail.*$option=([0-9]*).*$" "$pam_file" ; then # just change the value of option to a correct value sed -i --follow-symlinks "s/\(^auth.*[default=die].*pam_faillock.so.*authfail.*\)\($option *= *\).*/\1\2$value/" "$pam_file" # the option is not set. else # append the option sed -i --follow-symlinks "/^auth.*[default=die].*pam_faillock.so.*authfail.*/ s/$/ $option=$value/" "$pam_file" fi # auth default pam_faillock.so authfail is not present, insert the whole line else sed -i --follow-symlinks "/^auth.*sufficient.*pam_unix.so.*/a auth [default=die] pam_faillock.so authfail $option=$value" "$pam_file" fi }
function insert_account { local pam_file="$1" if ! grep -qE "^\s*account\s+required\s+pam_faillock\.so.*$" "$pam_file" ; then sed -E -i --follow-symlinks "/^\s*account\s*required\s*pam_unix.so/i account required pam_faillock.so" "$pam_file" fi }
function set_faillock_option { local pam_file="$1" local option="$2" local value="$3" insert_preauth "$pam_file" "$option" "$value" insert_authfail "$pam_file" "$option" "$value" insert_account "$pam_file" }

