fix-group.fix:
include_mount_options_functions

function perform_remediation {
	# test "$mount_has_to_exist" = 'yes'
	if test "no" = 'yes'; then
		assert_mount_point_in_fstab /dev/shm || { echo "Not remediating, because there is no record of /dev/shm in /etc/fstab" >&2; return 1; }
	fi

	ensure_mount_option_in_fstab "/dev/shm" "nosuid" "tmpfs" "tmpfs"

	ensure_partition_is_mounted "/dev/shm"
}

perform_remediation

include_mount_options_functions

function perform_remediation {
	# test "$mount_has_to_exist" = 'yes'
	if test "no" = 'yes'; then
		assert_mount_point_in_fstab /dev/shm || { echo "Not remediating, because there is no record of /dev/shm in /etc/fstab" >&2; return 1; }
	fi

	ensure_mount_option_in_fstab "/dev/shm" "nodev" "tmpfs" "tmpfs"

	ensure_partition_is_mounted "/dev/shm"
}

perform_remediation

SSSD_CONF="/etc/sssd/sssd.conf" SSSD_OPT="pam_cert_auth" SSSD_OPT_VAL=true PAM_REGEX="[[:space:]]*\[pam]" PAM_OPT_REGEX="${PAM_REGEX}([^
\[]*
+)+?[[:space:]]*${SSSD_OPT}"
if grep -qzosP $PAM_OPT_REGEX $SSSD_CONF; then sed -i "s/${SSSD_OPT}[^(
)]*/${SSSD_OPT} = ${SSSD_OPT_VAL}/" $SSSD_CONF elif grep -qs $PAM_REGEX $SSSD_CONF; then sed -i "/$PAM_REGEX/a ${SSSD_OPT} = ${SSSD_OPT_VAL}" $SSSD_CONF else mkdir -p /etc/sssd touch $SSSD_CONF echo -e "[pam]
${SSSD_OPT} = ${SSSD_OPT_VAL}" >> $SSSD_CONF fi

include_mount_options_functions

function perform_remediation {
	# test "$mount_has_to_exist" = 'yes'
	if test "no" = 'yes'; then
		assert_mount_point_in_fstab /dev/shm || { echo "Not remediating, because there is no record of /dev/shm in /etc/fstab" >&2; return 1; }
	fi

	ensure_mount_option_in_fstab "/dev/shm" "noexec" "tmpfs" "tmpfs"

	ensure_partition_is_mounted "/dev/shm"
}

perform_remediation
